import { Form, Image } from 'antd'
import { UploadChangeParam, UploadFile } from 'antd/lib/upload/interface'
import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { equipmentsCommonDetailsFormLayout } from '../../../../../layouts/portal/equipment/equipmentsCommonDetails.layout'
import { ETypeFormItem } from '../../../../../locales/portal/portalUsers.locals'
import { getSingleEquipment, isLoadingEquipments } from '../../../../../redux/selectors/equipments.selector'
import { verifyDocumentTypeImageAndSizeAction } from '../../../../../utils/files/verifyMimeType.util'
import { CustomForm } from '../../../../Common/CustomForm.component'
import { Nullish } from '../../../../Common/Nullish.component'

const inputs: (string|number)[] = [
]

export const CommonDetailsEdit = () => {

  const [file, setFile] = useState<UploadFile | null>(null)
  const [ hasChanged, setHasChanged ] = useState<boolean>(false);

  const equipment = useSelector(getSingleEquipment); 
  const isLoading = useSelector(isLoadingEquipments); 

  const [form] = Form.useForm();
  const initialValues = {
    name: equipment?.name,
    serial: equipment?.serial,
    activeFixedNumber: equipment?.fixedActiveNumber,
    description: equipment?.description,
  }

  useEffect(() => {
    form.setFieldsValue(initialValues);
  }, [equipment])

  const handleChange  = () => {
    setHasChanged(true);
  }

  const handleChangeFile = (e:UploadChangeParam<UploadFile<any>>) => {
    const result = verifyDocumentTypeImageAndSizeAction(e)
    setFile(result);
    if(result){
        setHasChanged(true); 
    }
  }

  const handleSubmit = (values: ISubmitValues) => {
    console.log(values)
    console.log(file)
  }

  let layout = equipmentsCommonDetailsFormLayout.map( (item) => {
    if(item.type === ETypeFormItem.INPUT){
        if(item.inputPrefix){
            item.propsInput = {
            ...item.propsInput!,
            prefix: <item.inputPrefix/> 
            }
        }
    }
    if(item.key === 'actual-image'){
        item.Cop = () => 
            <div className='form-common__item-image'>
                <Image
                    className='photo'
                    src={equipment?.photoUrl || 'error'}
                    fallback="data:image/png;base64,"
                    preview={{
                        mask: <Nullish/>
                    }}
                />
            </div>
    }
    
    if(item.type === ETypeFormItem.UPLOAD){
      item.Cop = () => <>Subir nueva imagen</>;
      item.propsInput = {
              ...item.propsInput,
              fileList: (file) ? [file] : [],
              onChange: (e:UploadChangeParam<UploadFile<any>>) => {
                handleChangeFile(e)
              },
              onRemove: () => {
                setFile(null);
              }
          }
      }
      if(item.type === ETypeFormItem.SAVE_BUTTON){
          item.propsInput = {
              ...item.propsInput,
              visible: hasChanged || file,
              loading: isLoading
          }
      }
    return item;
  }).filter(item => !inputs.includes(item.key))

  return (
      <CustomForm
        LAYOUT={layout}
        className='form-common--edit'
        form={form}
        initialValues={initialValues}
        handleSubmit={handleSubmit}
        onValuesChange={() => handleChange()}
      />
  )
}

interface ISubmitValues{
  name: string,
  serial: string,
  activeFixedNumber: string,
  description: string,
}
